           preloader.style.cssText = "background-color: rgb(171, 213, 150); background-repeat: no-repeat; background-position: center; background-size: cover; height: 100vh; position: fixed; top: 0; left: 0; z-index: 9999;";
           document.body.appendChild(preloader);
           window.addEventListener("load", function(){
               preloader.style.display = "none";
           });
       });
   </script>


</body>

</html>